// Seed function
async function seedDatabase() {
  console.log('='.repeat(60));
  console.log('SEEDING MCP DISCOVERY DATABASE');
  console.log(`Total servers to seed: ${MCP_SERVERS.length}`);
  console.log('='.repeat(60));

  const supabase = getSupabaseClient();
  let inserted = 0;
  let skipped = 0;

  for (const server of MCP_SERVERS) {
    // Check if exists
    const existing = await getServerBySlug(supabase, server.slug);
    if (existing) {
      skipped++;
      continue;
    }

    // Insert server
    const { error } = await supabase.from('mcp_servers').insert({
      name: server.name,
      slug: server.slug,
      npm_package: server.npm_package,
      github_url: server.github_url,
      description: server.description,
      install_command: server.install_command,
      docs_url: server.docs_url,
      homepage_url: server.homepage_url,
      category: server.category,
      is_verified: false,
    });

    if (error) {
      console.error(`Error inserting ${server.name}:`, error.message);
      continue;
    }

    inserted++;

    // Add capabilities
    for (const cap of server.capabilities) {
      await upsertCapability(supabase, cap, server.category);
    }

    if (inserted % 100 === 0) {
      console.log(`Progress: ${inserted} inserted, ${skipped} skipped`);
    }
  }

  console.log('\n' + '='.repeat(60));
  console.log('SEEDING COMPLETE');
  console.log('='.repeat(60));
  console.log(`Inserted: ${inserted}`);
  console.log(`Skipped: ${skipped}`);
}

seedDatabase().catch(console.error);
